class SidebarComponent extends HTMLElement{connectedCallback(){const e=window.location.pathname.split("/").pop(),t=`\n      <aside class="w-64 bg-white shadow-md border-r">\n        <figure class="p-6 mt-3">\n          <img src="../assets/images/logo-sibayak.svg" alt="Logo sibayak multi">\n        </figure>\n        <h1 class="px-6 text-xl font-bold mt-8">Dashboard</h1>\n        <nav class="mt-6">\n          ${this.linkItem("index.html","home","Home",e)}\n          ${this.linkItem("reservation-data.html","cs-data","Reservation Data",e)}\n          ${this.linkItem("room-data.html","roomdata","Room Data",e)}\n        </nav>\n      </aside>\n    `;this.innerHTML=t}linkItem(e,t,a,n){const s=e.includes("index")||e.includes(n);return`\n      <a href="./${e}" class="${"flex gap-2 px-6 py-3 "+(s?"font-medium bg-[#7C6A46] text-white":"text-gray-700 hover:bg-gray-100")}">\n        <img src="../assets/images/${t}${s?"-white":""}.svg" alt="">${a}\n      </a>\n    `}}customElements.define("sidebar-component",SidebarComponent);